// Tải dotenv với đường dẫn tuyệt đối và kiểm tra lỗi
const path = require('path');
const dotenv = require('dotenv');
const dotenvResult = dotenv.config({ path: path.resolve(__dirname, '.env') });
if (dotenvResult.error) {
    console.error('❌ Lỗi khi tải file .env:', dotenvResult.error.message);
    process.exit(1);
}

const express = require('express');
const mongoose = require('mongoose');
const cors = require('cors');
const cartRoute = require('./routes/cartRoute.js');
const productRoutes = require('./routes/productsRoute.js'); // Đảm bảo tên file khớp
const paymentRoutes = require('./routes/paymentRoutes.js');
const userRoutes = require('./routes/userRoute.js');

const app = express();
const port = 3001;

// Cấu hình CORS
const allowedOrigins = ['http://localhost:3000', 'http://localhost:5173'];
app.use(cors({
    origin: (origin, callback) => {
        if (!origin || allowedOrigins.includes(origin)) {
            callback(null, true);
        } else {
            callback(new Error(`Origin ${origin} not allowed by CORS`));
        }
    },
    credentials: true,
    methods: ['GET', 'POST', 'PUT', 'DELETE', 'OPTIONS'],
    allowedHeaders: ['Content-Type', 'Authorization']
}));

// Middleware xử lý JSON
app.use(express.json());

// Bật debug mode của Mongoose
mongoose.set('debug', true);

// Kết nối MongoDB
const mongoUri = process.env.MONGODB_URI || 'mongodb://127.0.0.1:27017/DATN';
mongoose.connect(mongoUri, {
    useNewUrlParser: true,
    useUnifiedTopology: true,
})
.then(() => console.log('✅ Kết nối thành công với MongoDB tại:', mongoUri))
.catch(err => console.error('❌ Lỗi kết nối MongoDB:', err));

// Phục vụ file tĩnh
app.use('/img', express.static('img'));

// Định tuyến
app.use('/api/product', productRoutes);
app.use('/api/cart', cartRoute);
app.use('/api/payments', paymentRoutes);
app.use('/api/user', userRoutes);

// Khởi động server
app.listen(port, () => {
    console.log(`🚀 Server đang chạy tại http://localhost:${port}`);
});

