      return res.status(401).json({
        success: false,
        message: "Token kh\xF4ng h\u1EE3p l\u1EC7: ".concat(error.message)
      });
    }
    return res.status(401).json({
      success: false,
      message: 'Lỗi xác thực token!'
    });
  }
};
